import React, { useState, useRef } from 'react';

const NewHome = () => {
	const allSpans = useRef([
		{
			span0: '',
			span1: '',
			span2: '',
			span3: ''
		}
	]);

	function toggler(id) {
		console.log(id);
	}

	return (
		<div className="headerMain">
			<div className="headerContentContainer">
				<h1> Hey, I'm Matt!</h1>
				<h3 className="lightText70 max-width700">
					I'm a
					<span className="off pointer transitionAll">senior product designer</span>, an energetic lad, a {''}
					<span
						onClick={() => {
							toggler(allSpans);
						}}
						ref={allSpans.span1}
						id="1"
						className="off pointer transitionAll"
					>
						coding enthusiast
					</span>, a {''}
					<span id="2" className="off pointer transitionAll">
						dog lover
					</span>
					{''} & a {''}
					<span id="3" className="off pointer transitionAll">
						waterfall fantastic
					</span>
					!
				</h3>

				<div>
					<p className="badge">Available for full-time opportunities </p>
				</div>
			</div>
		</div>
	);
};

export default NewHome;
