import React, { useState } from 'react';
import videoTexture from '../../assets/video/videoTexture.mp4';
import videoPoster from '../../assets/video/videoPoster.png';
import '../css/home.css';
import '../css/tackOn.css';
import '../css/base.css';

import GoNoodleLogo from '../../assets/logos/GoNoodleLogo.svg';

const NewHome = () => {
	const [ message, setMessage ] = useState('');
	const messageContent = [
		'Spent 5 epic years shipping products at Capital One, GoNoodle & My own startup! ',
		'I love using React to create flexible prototypes to test more effectively!',
		'I have two dogs, George (Puggle) & Cady (Boston Terrior) & I love them!',
		'Trying to checkout all 325 waterfalls in Tennessee! Or atleast 50% ðŸ˜…'
	];
	const [ trackedTimer, setTrackedTimers ] = useState([]);

	const workData = [
		{
			logo: GoNoodleLogo,
			company: 'GoNoodle',
			title: 'Product Designer'
		},
		{
			logo: GoNoodleLogo,
			company: 'GoNoodle',
			title: 'Product Designer'
		},
		{
			logo: GoNoodleLogo,
			company: 'GoNoodle',
			title: 'Product Designer'
		},
		{
			logo: GoNoodleLogo,
			company: 'GoNoodle',
			title: 'Product Designer'
		}
	];

	function toggler(id) {
		const getAllSpans = document.querySelectorAll('span');
		const convertSpans = Array.from(getAllSpans);
		convertSpans.map((cv) => {
			cv.classList.remove('on');
		});
		const chosenSpan = convertSpans[id];
		chosenSpan.classList.add('on');
		setMessage(messageContent[id]);
		const timer = setTimeout(() => {
			chosenSpan.classList.remove('on');
			setMessage('');
			console.log('inner', timer);
		}, 6000);
	}

	return (
		<div className="headerMain">
			{/* <video id="videoBG" poster={videoPoster} autoPlay muted loop>
				<source src={videoTexture} type="video/mp4" />
			</video> */}
			<div className="headerContentContainer max-width750">
				<h2> ðŸ¤™ Whats Up!</h2>
				<h1> Iâ€™m Matt, a Product Designer, code enthusiast, & dog lover!</h1>
				<h3 className=" max-width750">
					Iâ€™m obsessed with connecting the dots between user needs, business goals, and feasibility. I relish
					in refining processes, evolving products, and growing with a team. I love to simplify complexities
					and working with awesome humans to make dope products!
				</h3>

				<div>
					<p className="badge">â€¢ Available for full-time opportunities </p>
				</div>
			</div>
			<section className="workSection">
				<div className="workHeader">
					<h2 className="marginRight10"> My Work </h2>
					<h3> All projects link out to Medium articals</h3>
				</div>
			</section>
		</div>
	);
};

export default NewHome;

const ProjectCards = (props) => {
