import './view.css';
import './tackOn.css';
import './base.css';

const NewHome = () => {
	const [ message, setMessage ] = useState('');
	const messageContent = [
		'Spent 5 epic years shipping products at Capital One, GoNoodle & My own startup! ',
		'I love using React to create flexible prototypes to test more effectively!',
		'I have two dogs, George (Puggle) & Cady (Boston Terrior) & I love them!',
		'Trying to checkout all 325 waterfalls in Tennessee! Or atleast 50% ðŸ˜…'
	];
	const [ trackedTimer, setTrackedTimers ] = useState([]);

	function toggler(id) {
		const getAllSpans = document.querySelectorAll('span');
		const convertSpans = Array.from(getAllSpans);
		convertSpans.map((cv) => {
			cv.classList.remove('on');
		});
		const chosenSpan = convertSpans[id];
		chosenSpan.classList.add('on');
		setMessage(messageContent[id]);
		const timer = setTimeout(() => {
			chosenSpan.classList.remove('on');
			setMessage('');
			console.log('inner', timer);
		}, 6000);
	}

