import React, { useState } from 'react';
import videoTexture from '../../assets/video/videoTexture.mp4';
import videoPoster from '../../assets/video/videoPoster.png';

const NewHome = () => {
	const [ message, setMessage ] = useState('');
	const messageContent = [
		'Spent 5 epic years shipping products at Capital One, GoNoodle & My own startup! ',
		'I love using React to create flexible prototypes to test more effectively!',
		'I have two dogs, George (Puggle) & Cady (Boston Terrior) & I love them!',
		'Trying to checkout all 325 waterfalls in Tennessee! Or atleast 50% ðŸ˜…'
	];
	const [ trackedTimer, setTrackedTimers ] = useState([]);

	function toggler(id) {
		const getAllSpans = document.querySelectorAll('span');
		const convertSpans = Array.from(getAllSpans);
		convertSpans.map((cv) => {
			cv.classList.remove('on');
		});
		const chosenSpan = convertSpans[id];
		chosenSpan.classList.add('on');
		setMessage(messageContent[id]);
		const timer = setTimeout(() => {
			chosenSpan.classList.remove('on');
			setMessage('');
			console.log('inner', timer);
		}, 6000);
	}

	return (
		<div className="headerMain">
			<video id="videoBG" poster={videoPoster} autoplay muted loop>
				<source src={videoTexture} type="video/mp4" />
			</video>
			<div className="headerContentContainer">
				<h1> Hey, I'm Matt!</h1>
				<h3 className="lightText70 max-width700">
					I'm a {''}
					<span
						onClick={() => {
							toggler(0);
						}}
						id="0"
						className="off pointer transitionAll"
					>
						senior product designer
					</span>, an energetic lad, a {''}
					<span
						onClick={() => {
							toggler(1);
						}}
						id="0"
						id="1"
						className="off pointer transitionAll"
					>
						coding enthusiast
					</span>, a {''}
					<span
						onClick={() => {
							toggler(2);
						}}
						id="0"
						id="2"
						className="off pointer transitionAll"
					>
						dog lover
					</span>
					{''} & a {''}
					<span
						onClick={() => {
							toggler(3);
						}}
						id="0"
						id="3"
						className="off pointer transitionAll"
					>
						waterfall fantastic
					</span>
					!
				</h3>

				<div>
					<p className="badge">Available for full-time opportunities </p>
				</div>
			</div>
			<p className={message === '' ? 'opacity0 transitionAll' : 'message boxShadow max-width500 transitionAll'}>
				{message}{' '}
			</p>
		</div>
	);
};

export default NewHome;
