import React, { useEffect, useRef, useState } from "react";
import { NavLink } from "react-router-dom";
import { gsap } from "gsap";
import "./nav.css";
import logo from "../../assets/logo.svg";
import menu from "../../assets/menu.svg";
const Nav = () => {
  let fadeIn = useRef(null);

  useEffect(() => {
    gsap.from([fadeIn], {
      opacity: 0,
      delay: 0.5,
      ease: "power3.out",
      y: 0,
      stagger: {
        amount: 0.15
      }
    });
  }, [fadeIn]);

  const [toggle, setToggle] = useState(false);
  const toggler = () => {
    console.log(toggle);
    setToggle(!toggle);
  };
  return (
    <div ref={animation => (fadeIn = animation)} className="navContainer">
      <img src={logo}></img>
      <nav>
        <NavLink to="/" exact activeStyle={{ color: "#000" }}>
          WORK
        </NavLink>
        <NavLink to="/about" exact activeStyle={{ color: "#000" }}>
          ABOUT
        </NavLink>
        <button>RESUME</button>
      </nav>

      <img onClick={toggler} src={menu} className="menu" />

      <div className={toggle ? "menu_container" : "menu_container hidden_menu"}>
        <nav className={toggle ? "mobile" : "mobile_hidden"}>
          <NavLink onClick={toggler} to="/">
            Home
          </NavLink>
          <NavLink onClick={toggler} to="/all_challenges">
            All Challenges
          </NavLink>
          <NavLink onClick={toggler} to="/resources">
            Resources
          </NavLink>
        </nav>
      </div>
    </div>
  );
};

export default Nav;
