import React, { useState, useRef } from 'react';

const NewHome = () => {
	function toggler(id) {
		//get all of the span dom elements
		const getAllSpans = document.querySelectorAll('span');
		//convert from nodelist to array
		const convertSpans = Array.from(getAllSpans);
		console.log('convertSpans', convertSpans);
		//map over array of 4 spans
		const removeOnClass = convertSpans.map((cv) => {
			cv.classList.remove('on');
		});

		const chosenSpan = convertSpans[id];
		chosenSpan.classList.add('on');
	}

	return (
		<div className="headerMain">
			<div className="headerContentContainer">
				<h1> Hey, I'm Matt!</h1>
				<h3 className="lightText70 max-width700">
					I'm a
					<span
						onClick={() => {
							toggler(0);
						}}
						id="0"
						className="off pointer transitionAll"
					>
						senior product designer
					</span>, an energetic lad, a {''}
					<span id="1" className="off pointer transitionAll">
						coding enthusiast
					</span>, a {''}
					<span id="2" className="off pointer transitionAll">
						dog lover
					</span>
					{''} & a {''}
					<span id="3" className="off pointer transitionAll">
						waterfall fantastic
					</span>
					!
				</h3>

				<div>
					<p className="badge">Available for full-time opportunities </p>
				</div>
			</div>
		</div>
	);
};

export default NewHome;
